public class java5 {

	private static final String MAX_VALUE = null;

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Integer Minvalue: "+Integer .MIN_VALUE+"\t integer MaxValue:"+MAX_VALUE+ "\t integer size "+ Integer.SIZE +" bits");
        System.out.println("Short minvalue: "+Short .MIN_VALUE+"\t short MaxValue:"+MAX_VALUE+ "\t short size "+ Short.SIZE +" bits");
        System.out.println("float minvalue: "+Float .MIN_VALUE+"\t float maxvalue:"+MAX_VALUE+ "\t float size "+ Float.SIZE +" Bits");
        System.out.println("double minvalue: "+Double .MIN_VALUE+"\t double maxvalue: "+MAX_VALUE+ "\t double size "+ Double.SIZE +" bits");
        System.out.println("character minvalue: "+Character .MIN_VALUE+"\t character maxvalue: "+MAX_VALUE+ "\t character size "+ Character.SIZE +" Bits");
        System.out.println("long minvalue: "+Long .MIN_VALUE+"\t long maxvalue: "+MAX_VALUE+ "\t long size "+ Long.SIZE +" Bits");
        System.out.println("byte minvalue: "+Byte .MIN_VALUE+"\t byte maxvalue: "+MAX_VALUE+ "\t byte size "+ Byte.SIZE +" Bits");
        
	}
}